var debug = false;

function initEventHandlers() {
	if(debug){
		console.log('initing event handlers');
	}
	/* 
		Document query selector doesn't return an array. Use array.prototype method for each. 
		Select every data-openselector and bind an evenlistener to it.
	*/
  Array.prototype.forEach.call( document.querySelectorAll('[data-openselector]'), bindToggleOpen );
}

function bindToggleOpen( element ) {
	element.addEventListener( 'click', toggleOpen );
}

function toggleOpen(){
	var openElement = document.querySelector( this.dataset.openselector );
	
	this.classList.toggle('open');
	openElement.classList.toggle('open');
	document.body.classList.toggle('noScroll');
}

function adjustWindow(){
    // Get window size
    winH = window.innerHeight;
    winW = window.innerWidth;

    // Keep minimum height 550
    if(winH <= 550) {
        winH = 550;
    }

    // Init Skrollr for 768 and up
    if( winW >= 768) {

        // Init Skrollr
        var s = skrollr.init({
            forceHeight: false
        });
    } else {

        // BREAK SKROLLER.INIT IF THE SCREEN SIZE IS BELOW 768
        var s = skrollr.init();
        s.destroy();
    }
	
	// Check for touch
   	if(Modernizr.touch) {

		// BREAK SKROLLER.INIT IF THE SCREEN SIZE IS A TOUCH DEVICE
		var s = skrollr.init();
		s.destroy();
   	}
}

function initAdjustWindow() {
    return {
        match : function() {
            adjustWindow();
        },
        unmatch : function() {
            adjustWindow();
        }
    };
}
var typingText = document.getElementById('typingText');
if (typingText !=null) {
var myArray = typingText.dataset.text.split(""),
			loopTimer;
}
function frameLooper() {
  if(myArray.length > 0) {
    document.getElementById("typingText").innerHTML += myArray.shift();
  } else {
    clearTimeout(loopTimer);
                return false;
  }
  loopTimer = setTimeout('frameLooper()',100);
}

function loadMasonry(){
	var elem = document.querySelector('.projects');
	var msnry = new Masonry( elem, {
	  // options
	  itemSelector: '.projectItem'
	});

	// element argument can be a selector string
	//   for an individual element
	var msnry = new Masonry( '.projects', {
	  columnWidth: '.grid-sizer',
		itemSelector: '.projectItem',
		percentPosition: true
	});
}

function init(){
	enquire.register("screen and (min-width : 768px)", initAdjustWindow(), false);
	initEventHandlers();

	if (typingText !=null) {
		frameLooper();
	}
	loadWayPoint();
}

function loadWayPoint(){
	var hamburger_btn = document.querySelector('.hamburger-menu'),
			machine_btn = document.querySelector('.machine_btn'),
			home_btn = document.querySelector('.home_btn'),

			waypoint = new Waypoint({
	  		element: document.querySelector('.changecolorblack'),
	  		handler: function() {
	    		hamburger_btn.classList.toggle("black");
					machine_btn.classList.toggle("black");
					home_btn.classList.toggle("black");
	  		},
  			offset: 200
			});
};


document.addEventListener("DOMContentLoaded", init);
